/**
 * Buscar faturas por per√≠odo
 * @param {Date} startDate - Data de in√≠cio
 * @param {Date} endDate - Data de fim
 * @returns {Promise<Array>} - Lista de faturas no per√≠odo
 */
export const getInvoicesByPeriod = async (startDate, endDate) => {
  try {
    const q = query(
      collection(db, COLLECTIONS.INVOICES),
      where('dueDate', '>=', Timestamp.fromDate(startDate)),
      where('dueDate', '<=', Timestamp.fromDate(endDate)),
      orderBy('dueDate', 'desc')
    );
    
    const querySnapshot = await getDocs(q);
    const invoices = [];
    
    querySnapshot.forEach((doc) => {
      invoices.push({ id: doc.id, ...doc.data() });
    });
    
    return invoices;
  } catch (error) {
    console.error('‚ùå Erro ao buscar faturas por per√≠odo:', error);
    throw error;
  }
};

