/**
 * Calcular pr√≥xima data de cobran√ßa baseada no ciclo
 * @param {Date} startDate - Data de in√≠cio
 * @param {string} billingCycle - Ciclo de cobran√ßa
 * @returns {Date} - Pr√≥xima data de cobran√ßa
 */
const calculateNextBillingDate = (startDate, billingCycle) => {
  const date = new Date(startDate);
  
  switch (billingCycle) {
    case 'monthly':
      date.setMonth(date.getMonth() + 1);
      break;
    case 'quarterly':
      date.setMonth(date.getMonth() + 3);
      break;
    case 'semiannual':
      date.setMonth(date.getMonth() + 6);
      break;
    case 'annual':
      date.setFullYear(date.getFullYear() + 1);
      break;
    default:
      date.setMonth(date.getMonth() + 1);
  }
  
  return date;
};

/**
 * Gerar faturas para assinaturas do m√™s
 * @param {number} month - M√™s (0-11)
 * @param {number} year - Ano
 */
export const generateInvoicesForMonth = async (month, year) => {
  try {
    console.log(`üîÑ Gerando faturas para ${month + 1}/${year}`);
    
    // Buscar assinaturas ativas
    const subscriptionsQuery = query(
      collection(db, COLLECTIONS.SUBSCRIPTIONS),
      where('status', '==', 'active')
    );
    
    const subscriptionsSnapshot = await getDocs(subscriptionsQuery);
    const batch = writeBatch(db);
    let invoicesCreated = 0;
    
    for (const subscriptionDoc of subscriptionsSnapshot.docs) {
      const subscription = { id: subscriptionDoc.id, ...subscriptionDoc.data() };
      
      // Verificar se deve gerar fatura neste m√™s
      if (shouldGenerateInvoiceForMonth(subscription, month, year)) {
        // Verificar se fatura j√° existe
        const existingInvoiceQuery = query(
          collection(db, COLLECTIONS.INVOICES),
          where('subscriptionId', '==', subscription.id),
          where('month', '==', month),
          where('year', '==', year)
        );
        
        const existingInvoices = await getDocs(existingInvoiceQuery);
        
        if (existingInvoices.empty) {
          // Criar nova fatura
          const dueDate = new Date(year, month, subscription.billingDay || 5);
          const invoiceRef = doc(collection(db, COLLECTIONS.INVOICES));
          
          batch.set(invoiceRef, {
            clientId: subscription.clientId,
            subscriptionId: subscription.id,
            amount: subscription.amount,
            description: subscription.description,
            dueDate: Timestamp.fromDate(dueDate),
            status: 'pending',
            month,
            year,
            createdAt: Timestamp.now(),
            updatedAt: Timestamp.now()
          });
          
          invoicesCreated++;
        }
      }
    }
    
    if (invoicesCreated > 0) {
      await batch.commit();
      console.log(`‚úÖ ${invoicesCreated} faturas geradas com sucesso`);
    } else {
      console.log('‚ÑπÔ∏è Nenhuma fatura nova para gerar');
    }
    
    return invoicesCreated;
  } catch (error) {
    console.error('‚ùå Erro ao gerar faturas:', error);
    throw error;
  }
};

/**
 * Verificar se deve gerar fatura para o m√™s
 * @param {Object} subscription - Dados da assinatura
 * @param {number} month - M√™s (0-11)
 * @param {number} year - Ano
 * @returns {boolean}
 */
const shouldGenerateInvoiceForMonth = (subscription, month, year) => {
  const startDate = subscription.startDate.toDate();
  const currentDate = new Date(year, month, 1);
  
  // Assinatura deve ter iniciado
  if (startDate > currentDate) {
    return false;
  }
  
  // Verificar ciclo de cobran√ßa
  const startMonth = startDate.getMonth();
  const startYear = startDate.getFullYear();
  
  switch (subscription.billingCycle) {
    case 'monthly':
      return true;
    case 'quarterly':
      return (month - startMonth) % 3 === 0;
    case 'semiannual':
      return (month - startMonth) % 6 === 0;
    case 'annual':
      return month === startMonth;
    default:
      return true;
  }
};

