// src/services/firestore.js
import {
  collection,
  addDoc,
  getDocs,
  doc,
  updateDoc,
  query,
  orderBy,
  where,
  writeBatch,
  serverTimestamp,
  onSnapshot // Adicione esta importa√ß√£o
} from 'firebase/firestore';
import { db } from './firebase';

// Constantes das cole√ß√µes
export const COLLECTIONS = {
  CLIENTS: 'clients',
  SUBSCRIPTIONS: 'subscriptions',
  INVOICES: 'invoices'
};

// ***** CLIENTES *****
export const clientService = {
  // Criar cliente
  async create(clientData) {
    try {
      const docRef = await addDoc(collection(db, COLLECTIONS.CLIENTS), {
        ...clientData,
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp()
      });
      console.log('‚úÖ Cliente criado:', docRef.id);
      return { success: true, id: docRef.id };
    } catch (error) {
      console.error('‚ùå Erro ao criar cliente:', error);
      return { success: false, error: error.message };
    }
  },

  // Listar todos os clientes
  async getAll() {
    try {
      const q = query(
        collection(db, COLLECTIONS.CLIENTS),
        orderBy('createdAt', 'desc')
      );
      const querySnapshot = await getDocs(q);
      const clients = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      console.log('‚úÖ Clientes carregados:', clients.length);
      return clients;
    } catch (error) {
      console.error('‚ùå Erro ao buscar clientes:', error);
      return [];
    }
  },
  
  // CORRE√á√ÉO: Adicionar o m√©todo 'subscribe'
  subscribe(callback) {
    const q = query(
      collection(db, COLLECTIONS.CLIENTS),
      orderBy('createdAt', 'desc')
    );
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const clients = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      callback(clients);
    });
    return unsubscribe;
  },

  // Atualizar cliente
  async update(clientId, clientData) {
    try {
      const clientRef = doc(db, COLLECTIONS.CLIENTS, clientId);
      await updateDoc(clientRef, {
        ...clientData,
        updatedAt: serverTimestamp()
      });
      console.log('‚úÖ Cliente atualizado:', clientId);
      return { success: true };
    } catch (error) {
      console.error('‚ùå Erro ao atualizar cliente:', error);
      return { success: false, error: error.message };
    }
  },

  // Deletar cliente e dados relacionados
  async delete(clientId) {
    try {
      const batch = writeBatch(db);

      // Deletar assinaturas relacionadas
      const subscriptionsQuery = query(
        collection(db, COLLECTIONS.SUBSCRIPTIONS),
        where('clientId', '==', clientId)
      );
      const subscriptionsSnapshot = await getDocs(subscriptionsQuery);
      subscriptionsSnapshot.docs.forEach(doc => {
        batch.delete(doc.ref);
      });

      // Deletar faturas relacionadas
      const invoicesQuery = query(
        collection(db, COLLECTIONS.INVOICES),
        where('clientId', '==', clientId)
      );
      const invoicesSnapshot = await getDocs(invoicesQuery);
      invoicesSnapshot.docs.forEach(doc => {
        batch.delete(doc.ref);
      });

      // Deletar cliente
      const clientRef = doc(db, COLLECTIONS.CLIENTS, clientId);
      batch.delete(clientRef);
      
      await batch.commit();
      console.log('‚úÖ Cliente deletado:', clientId);
      return { success: true };
    } catch (error) {
      console.error('‚ùå Erro ao deletar cliente:', error);
      return { success: false, error: error.message };
    }
  }
};

// ***** ASSINATURAS *****
export const subscriptionService = {
  // Criar assinatura
  async create(subscriptionData) {
    try {
      const docRef = await addDoc(collection(db, COLLECTIONS.SUBSCRIPTIONS), {
        ...subscriptionData,
