// src/App.js (Versão Corrigida e Melhorada)
import React, { useState, useEffect } from 'react';
import { useFirebaseAuth } from './hooks/useFirebaseAuth';
import { clientService, subscriptionService, invoiceService, seedService } from './services/firestore';
import { ROUTES } from './utils/constants';

// Componentes
import Header from './components/common/Header';
import LoadingSpinner from './components/common/LoadingSpinner';
import LoginPage from './components/auth/LoginPage';
import FirebaseSetup from './components/auth/FirebaseSetup';
import Dashboard from './components/dashboard/Dashboard';
import ClientsPage from './components/clients/ClientsPage';
import ReportsPage from './components/reports/ReportsPage';

// Estilos
import './styles/globals.css';

function App() {
  const { user, loading: authLoading, signIn, logout } = useFirebaseAuth();
  const [currentView, setCurrentView] = useState(ROUTES.DASHBOARD);
  const [dataLoading, setDataLoading] = useState(false);
  const [error, setError] = useState(null); // Novo estado para erros

  // Estados dos dados
  const [clients, setClients] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);
  const [invoices, setInvoices] = useState([]);

  // Verificar se Firebase está configurado
  const isFirebaseConfigured = () => {
    const requiredVars = ['REACT_APP_FIREBASE_API_KEY', 'REACT_APP_FIREBASE_PROJECT_ID'];
    return requiredVars.every(envVar => process.env[envVar]);
  };

  // Carregar dados do usuário
  const loadUserData = async () => {
    if (!user) return;
    setDataLoading(true);
    setError(null); // Limpa erros anteriores
    try {
      const [clientsData, subscriptionsData, invoicesData] = await Promise.all([
        clientService.getAll(),
        subscriptionService.getAll(),
        invoiceService.getAll()
      ]);
      setClients(clientsData);
      setSubscriptions(subscriptionsData);
      setInvoices(invoicesData);
    } catch (e) {
      console.error('Erro ao carregar dados do usuário:', e);
      setError('Falha ao carregar os dados. Verifique suas regras de segurança do Firestore e a conexão com a internet.');
    } finally {
      setDataLoading(false);
    }
  };

  // Criar dados de exemplo
  const handleCreateSampleData = async () => {
    if (!user) return;
    if (clients.length > 0) {
      alert('Dados de exemplo já existem!');
      return;
    }
    setDataLoading(true);
    setError(null);
    try {
      const result = await seedService.createSampleData();
      if (result.success) {
        await loadUserData();
        alert('Dados de exemplo criados com sucesso!');
      } else {
        alert('Erro ao criar dados de exemplo: ' + result.error);
      }
    } catch (e) {
      console.error('Erro ao criar dados de exemplo:', e);
      alert('Erro ao criar dados de exemplo');
      setError('Falha ao criar dados de exemplo.');
    } finally {
      setDataLoading(false);
    }
  };

  // Carregar dados quando usuário faz login
  useEffect(() => {
    if (user) {
      loadUserData();
    } else {
      // Limpar dados quando usuário faz logout
      setClients([]);
      setSubscriptions([]);
      setInvoices([]);
    }
  }, [user]);

  // Loading inicial da autenticação
  if (authLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <LoadingSpinner size="large" message="Carregando Autenticação..." />
      </div>
    );
  }

  // Firebase não configurado
  if (!isFirebaseConfigured()) {
    return <FirebaseSetup />;
  }

  // Usuário não autenticado
  if (!user) {
    return <LoginPage onLogin={signIn} />;
  }

  // Se houver um erro no carregamento dos dados, exibe a mensagem
  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center text-center p-4">
        <div>
          <h2 className="text-xl font-bold text-red-600 mb-4">Ocorreu um Erro</h2>
          <p className="text-gray-700">{error}</p>
          <button
            onClick={loadUserData}
            className="mt-6 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Tentar Novamente
          </button>
        </div>
      </div>
    );
  }

  // Aplicação principal
  return (
    <div className="min-h-screen bg-gray-50">
      <Header
        user={user}
        onLogout={logout}
        currentView={currentView}
        onViewChange={setCurrentView}
        onCreateSampleData={handleCreateSampleData}
      />
      <main className="container mx-auto px-4 py-8">
        {dataLoading ? (
          <div className="text-center py-12">
            <LoadingSpinner size="medium" message="Carregando dados..." />
          </div>
        ) : (
          <>
            {currentView === ROUTES.DASHBOARD && <Dashboard invoices={invoices} setInvoices={setInvoices} clients={clients} />}
            {currentView === ROUTES.CLIENTS && <ClientsPage clients={clients} setClients={setClients} subscriptions={subscriptions} setSubscriptions={setSubscriptions} />}
            {currentView === ROUTES.REPORTS && <ReportsPage invoices={invoices} clients={clients} />}
          </>
        )}
      </main>
    </div>
  );
}

export default App;
